import requests

url = 'https://api.open-meteo.com/v1/forecast'
params = {'latitude': latitude, 'longitude': longitude, 'temperature_unit': temperature_unit, 'wind_speed_unit': wind_speed_unit, 'precipitation_unit': precipitation_unit, 'timeformat': timeformat, 'timezone': timezone, 'past_days': past_days, 'forecast_days': forecast_days}
if hourly: params["hourly"] = ",".join(hourly)
if daily: params["daily"] = ",".join(daily)
if current: params["current"] = ",".join(current)
response = requests.get(url, params=params)
print(response.json())
